mapper.select:
SELECT
FROM dashboards d INNER JOIN active_dashboards ad on d.id=ad.dashboard_id LEFT OUTER JOIN users u on u.id=ad.user_id WHERE d.is_global=${_true}
AND u.login IS NULL
AND u.login=#{login}
ORDER BY order_index ASC

SELECT
FROM dashboards d INNER JOIN active_dashboards ad on d.id=ad.dashboard_id LEFT OUTER JOIN users u on u.id=ad.user_id WHERE d.is_global=${_false}
AND u.login IS NULL
AND u.login=#{login}
ORDER BY order_index ASC
