mapper.select:
select

from characteristics c
where c.enabled=${_true}

select

from characteristics c

  and c.parent_id=#{parent_id}
  and c.enabled=${_true}

select

from characteristics c

  and c.kee=#{key}
  and c.enabled=${_true}

select

from characteristics c

  and c.id=#{id}
  and c.enabled=${_true}

select

from characteristics c

  and c.name=#{name}
  and c.enabled=${_true}
