mapper.select:
select, count(total_issues.id) as totalIssues, count(open_issues.id) as unresolvedIssues from action_plans ap left join projects p on p.id = ap.project_id left join issues total_issues on total_issues.action_plan_key = ap.kee left join issues open_issues on open_issues.id = total_issues.id and open_issues.resolution is null
and ap.project_id = #{projectId}
group by ap.id, ap.kee, ap.name, ap.description, ap.user_login, ap.project_id, ap.status, ap.deadline, ap.created_at, ap.updated_at, p.kee, p.uuid
