mapper.select:
SELECT
FROM groups g INNER JOIN groups_users gu on gu.group_id=g.id INNER JOIN users u on u.id=gu.user_id
u.login=#{login}

mapper.update:
UPDATE groups SET
name=#{name},
description=#{description},
updated_at=#{updatedAt}
WHERE id=#{id}

mapper.select:
SELECT count(g.id)
FROM groups g
WHERE UPPER(g.name) LIKE #{query}
